import { DatePipe } from '@angular/common';
import { Component } from '@angular/core';

@Component({
  selector: 'app-pipe',
  templateUrl: './pipe.component.html',
  styleUrls: ['./pipe.component.css']
})
export class PipeComponent {
msg:string="Good Afternoon";

employeeObj : any ={
    empName: "Tina",
    Salary: 900000,
    Designation: "Angular Developer",
    Hobbies: ["Dance", " writting", "reading", "Painting"]
}

today: number = Date.now();

studntoyeeObj : any ={
    StdName: "Tina",
    city:"Pune",
    age: 23,
    marks: 90
  };
}

<p>pipe works!</p>
<h2>Pipe Example</h2>

<h2>{{msg | uppercase}}</h2>

<p>Employee Designation: {{employeeObj.Designation | uppercase}}</p>

<p>Today is {{today | date}}</p>
<p>Full date is, {{today | date:'fullDate'}}</p>
<p>The time is {{today | date:'h:mm a z'}}</p>
