@Component({
  selector: 'app-directive-practice',
  templateUrl: './directive-practice.component.html',
  styleUrls: ['./directive-practice.component.css']
})
export class DirectivePracticeComponent {
age:number = 18;
isShow:boolean = false;

displayMoreContent(){
 this.isShow = true;
}

removeContent(){
  this.isShow = false;
 }
}
