  selector: 'app-assignment',
  templateUrl: './assignment.component.html',
  styleUrls: ['./assignment.component.css']
})
export class AssignmentComponent {
// Assignment 1
num1:number=0;
num2:number=0;
result:number=0;

add(){
  this.result = +this.num1 + +this.num2
}

sub(){
  this.result = this.num1 - this.num2
}

mult(){
  this.result = this.num1 * this.num2
}

div(){
  // this.result = this.num1 / this.num2
  if (this.num2 !== 0) {
    this.result = this.num1 / this.num2;
  } else {
    alert("error= division by zero");
  }

}

