import { Component } from '@angular/core';

@Component({
  selector: 'app-data-binding',
  templateUrl: './data-binding.component.html',
  styleUrls: ['./data-binding.component.css']
})
export class DataBindingComponent {
  // userName:string ="David";
  // useremailId:string = "david@gmail.com";

  // employeeObj : any ={
  //   empName: "Tina",
  //   Salary: 900000,
  //   Designation: "Angular Developer",
  //   Hobbies: ["Dance", " writting", "reading", "Painting"]
  // }

  // imgPath:string="data:image/png;base64,"

  // isDisabled:boolean = false;

  // isbtnDisabled:boolean = false;


 counter: number = 0;
 firstName:string = "";

  btnClick() {
    console.log("Button Click");
  }

  Increment() {
    if(this.counter < 6)
      this.counter++;
    }

  Decrement(){
    if(this.counter > 0)
      this.counter--;
  }  

  // modifyFname(){
  //   console.log("modify change")
  // }


  modifyFname(event:any){
    console.log("entered value", event.target.value)
    this.firstName=event.target.value
  }
}
