import { Component } from '@angular/core';

@Component({
  selector: 'app-attribute-directive',
  templateUrl: './attribute-directive.component.html',
  styleUrls: ['./attribute-directive.component.css']
})
export class AttributeDirectiveComponent {
  products:Product[] =[
    {productName:"Laptop", price:60000 , quantity:2},
    {productName:"POCO X2", price:30000 , quantity:1},
    {productName:"One Plus Nord", price:55000 , quantity:2},
    {productName:"LED TV", price:100000 , quantity:1}
]

}
interface Product {
 productName:string;
 price:number;
 quantity:number;
}

<p>attribute-directive works!</p>

<div class="container">
    <table class="table table-bordered text-center">
        <thead>
            <!-- tr pe kyu apply kiya? bcz us table ke under multipe tr create krke use usse UI pe display krna hai -->
          <tr>
            <th scope="col">Sr No.</th>
            <th scope="col">Product Name</th>
            <th scope="col">Price</th>
            <th scope="col">Quantity</th>
          </tr>
        </thead>
        <tbody>
           <ng-container *ngFor="let product of products; let i=index"> 
          <tr>
            <th>{{i+1}}</th>
            <td>{{product.productName}}</td>
            <td>{{product.price}}</td>
            <td>{{product.quantity}}</td>
          </tr>
        </ng-container>
        </tbody>
      </table>
</div>
